import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/project-img1.png";
import projImg2 from "../assets/img/project-img2.png";
import projImg3 from "../assets/img/project-img3.png";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {

  const projects = [
    {
      title: "WhatsApp Clone",
      description: "React Native + Express + MongoDB. Real-time chat with WebSocket, push notifications, and contact sync.",
      imgUrl: projImg1,
    },
    {
      title: "To-Do App",
      description: "Simple & clean productivity app using React Native and Redux for state management.",
      imgUrl: projImg2,
    },
    {
      title: "Hospital App",
      description: "Full-stack app with separate login for hospitals, doctors, and patients. Built using React Native and Node.js.",
      imgUrl: projImg3,
    },
    {
      title: "CRM - The French Cake Company",
      description: "Custom CRM with lead tracking, form automation, and integration with Zoho. Used in real production.",
      imgUrl: projImg1,
    },
  ];

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn": ""}>
                <h2>Projects</h2>
                <p>Here are some real-world and personal projects I've built over the last 8 months to sharpen my skills in React Native, MERN stack, and CRM systems.</p>
                <Tab.Container id="projects-tabs" defaultActiveKey="first">
                  <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                    <Nav.Item>
                      <Nav.Link eventKey="first">All Projects</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="second">Client Work</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="third">Personal Projects</Nav.Link>
                    </Nav.Item>
                  </Nav>
                  <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                    <Tab.Pane eventKey="first">
                      <Row>
                        {projects.map((project, index) => (
                          <ProjectCard
                            key={index}
                            {...project}
                          />
                        ))}
                      </Row>
                    </Tab.Pane>
                    <Tab.Pane eventKey="second">
                      <Row>
                        <ProjectCard
                          title="CRM - The French Cake Company"
                          description="Lead management, automation, and Zoho integration. Used in live production by the client."
                          imgUrl={projImg1}
                        />
                      </Row>
                    </Tab.Pane>
                    <Tab.Pane eventKey="third">
                      <Row>
                        <ProjectCard
                          title="WhatsApp Clone"
                          description="Real-time messaging, push notifications, and contact sync using React Native, Express & MongoDB."
                          imgUrl={projImg1}
                        />
                        <ProjectCard
                          title="Hospital App"
                          description="Role-based system for doctors, patients, and hospitals using MERN stack and secure authentication."
                          imgUrl={projImg3}
                        />
                        <ProjectCard
                          title="To-Do App"
                          description="Cross-platform productivity app using React Native and Redux Toolkit."
                          imgUrl={projImg2}
                        />
                      </Row>
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="background" />
    </section>
  );
}
